import React from "react";
import { useState } from "react";
import { motion } from "framer-motion";
import Box from "../Components/box";
import hotelImg1 from "../Images/pexels-michael-block-3225531.jpg";
import hotelImg2 from "../Images/pexels-pixabay-53464.jpg";
import hotelImg3 from "../Images/pexels-pixabay-277572.jpg";
import hotelImg4 from "../Images/pexels-valeria-boltneva-827528.jpg";
import ImageCarousel from "../Components/ImageCarousel";
import { Waypoint} from "react-waypoint";
import '../Components/box.css';
import Hamburger from 'hamburger-react'

export default function Page2(){
  const [isOpen, setOpen] = useState(false)
  const [isClicked,setIsClicked]=useState(false);
  const [isWaypointInView, setIsWaypointInView] = useState(true);
  const customToggle=()=>{
    
    setOpen(!isOpen);
    console.log(isOpen);
    
    if(!isOpen){
      console.log("menu opened")
      setIsClicked(true);
    }
    else{
      console.log("menu closed")
      setIsClicked(false);
    }
   
  } 

  const handleEnter = () => {
        
    setIsWaypointInView(false);
  
            // Do something when the waypoint enters the viewport
  };

  const handleLeave = () => {
    
   
    setIsWaypointInView(true);
    // Do something when the waypoint leaves the viewport
  };
  
   
    return(
        <div>
        <Waypoint onEnter={handleEnter} onLeave={handleLeave}>
         <div></div>
        </Waypoint>
           
<div className={isClicked ? "simple lightBlue" : "simple"}>hey</div>


           <div className={isWaypointInView ? "container show" : "container"}>
           <Hamburger toggled={isOpen} toggle={customToggle} color={isOpen ? "red" : "dodgerblue"} size={200}/>
             
              <div ></div>
           </div>
         
            
           
        </div>
    )
        
    
}
