import axios from "axios";
import React, { useEffect, useState } from "react";


export default function Img(){
    let hasAvailableRoom=true

    const [roomData,setRoomData]=useState([]);
    const [roomId,setRoomId]=useState('');
    useEffect(()=>{
        axios.get('http://localhost:5555/Rooms')
          .then((res)=>{
         /*  console.log(res.data) */
           setRoomData(res.data);
           
            })
          .catch((err)=>console.log(err))
      }) 

      const changeAvailable=(event)=>{
          const id=event.target.id;
          setRoomId(id);
          axios.put(`http://localhost:5555/Rooms/:${roomId}`,async(req,res)=>{
            try{
                
                /*code for update availability*/

                
            
            
            }
            catch(err){
               return res.send({message:err.message})
            }
          })
          
      }



      return(
        <div>
            {roomData.map((room)=>{
                if(!room.availability){
                    hasAvailableRoom=true;
                  }
                return(
                    <div key={room._id}>
                       <h1>Available Rooms</h1>
                       {hasAvailableRoom ? <div>
                         
                         <form>
                            <input type="radio" id={room._id} onChange={(e)=>changeAvailable(e)}/>
                            <label for={room._id}>
                              <div className="cards-cont">
                                <img src={room.url}/>
                                <h1>Room Info</h1>
                                <h2>Room ID:{room._id}</h2>
                                <h2>Availability:{(room.availability) ? <span>true</span>: <span>false</span>}</h2>
                             </div>
                            </label>
                          </form>
                     
                       </div> : 
                        <span>Sorry</span>  
                        
                        
                        
                        
                      }


                    </div>
                )
            })}
        </div>
      )
}
