import axios from "axios";
import React, { useEffect, useState } from "react";


export default function Img(){
    let hasAvailableRoom=true
    let i=0;
    const [roomData,setRoomData]=useState([]);
    const [roomId,setRoomId]=useState('');
    const [roomUrl,setRoomUrl]=useState('');
    
    
    const [rooms, setRooms] = useState([]);
   
    useEffect(()=>{
        axios.get('http://localhost:5555/Rooms')
          .then((res)=>{
        
           setRoomData(res.data);
           
           
            })
          .catch((err)=>console.log(err))
      }) 

      
          const handleRadioChange = (id) => {
        
            setRoomId(id);
           
            
          
              if (!rooms.includes(id)) {
                setRooms(prevIds => [...prevIds, id]);
               } else {
                 setRooms(prevIds => prevIds.filter(room => room !== id));
               }
        
            
        };
        
        const showRoom=()=>{
         
          rooms.forEach((id)=>{
            const data={
              url:roomUrl,
              availability:false
              
            }
            axios.put(`http://localhost:5555/Rooms/${id}`,data)
                .then(()=>{alert("room is booked!")})
                .catch(()=>{alert("somthing is wrong!")})
        
          })
    }

      return(
        <div>
           Available Rooms
            {roomData.map((room)=>{
                let hasAvailableRoom = room.availability;
                
              
                return(
                    <div key={room._id}>
                      
                       {hasAvailableRoom ? 
                            <div>
                         
                              <form>
                              {/*   {inputType ?
                                      <div>
                                        <input 
                                              type='radio' 
                                              id={room._id}
                                              name='room'
                                              checked={ selectedRoom === room._id}
                                              onChange={() => handleRadioChange(room._id)} 
                                          />
                                          <label for={room._id}>
                                              <img src={room.url}/>
                                          </label>
                                      </div>
                                      :

                                     
                                  } */}
                                   <div>
                                        <input 
                                              type='checkBox' 
                                              id={room._id}
                                              name='room'
                                            
                                              onChange={() => handleRadioChange(room._id)} 
                                          />
                                          <label for={room._id}>
                                              <img src={room.url}/>
                                              <h2>Room ID:{room._id}</h2>
                                              <h2>Availblity: {(room.availability) ? 'true' : 'false'}</h2>
                                          </label>
                                      </div>
                              </form>
                     
                            </div> 
                            : 
                            <span>Sorry</span>  
                        
                        
                        
                        
                      }
                      
                    

                    </div>
                )
            })}
          <button onClick={showRoom}>update</button>
        </div>
      )
}
