import './book.css';
import Navbar from '../Components/navbar.js';
import {useEffect, useState} from 'react';
import Room1 from '../Images/room1.jpg';
import Room2 from '../Images/room2.png';
import axios from 'axios';
export default function Book(){
    const [firstName,setFirstName]=useState('')
    const [lastName,setLastname]=useState('')
    const [mail,setMail]=useState('')
    const [guestCount,setGuestCount]=useState();
    const [arrivalDate,setArrivalDate]=useState('');
    const [arrivalTime,setArrivalTime]=useState('');

    const [pickup,setPickup]=useState(true)
    const [contactNo,setContactNo]=useState();
    const [departureDate,setDepartureDate]=useState('');
    const [departureTime,setDepartureTime]=useState('');
    const [roomId,setRoomId]=useState([]);
    const [isClicked,setIsClicked]=useState(false);
    const[inputType,changeInputType]=useState('radio');
    const[roomInfo,setRoomInfo]=useState([]);
    const change=()=>{
     
      if(inputType==='radio'){
        changeInputType('checkBox')
      }
      if(inputType==='checkBox'){
        changeInputType('radio')
      }
    }
    const getID=(event)=>{
      const id=event.target.id;
      if (!roomId.includes(id)) {
        setRoomId(prevIds => [...prevIds, id]);
      } else {
        setRoomId(prevIds => prevIds.filter(room => room !== id));
      }
    
    }
    useEffect(()=>{
      {console.log(`Room:${roomId}`)}
    }
    )
    
    //get rooms info
    useEffect(()=>{
      axios('http://localhost:5555/Rooms')
      .then((res)=>{
            setRoomInfo(res.data);
            console.log(res.data)
      })
      .catch((err)=>{
        console.log(err.res);
      })
    },[])
    
    
    
    const createBooking=()=>{
      const data={
        firstName,
        lastName,
        mail,
        guestCount,
        arrivalDate,
        arrivalTime,
        departureDate,
        departureTime,
        pickup,
        contactNo,
        roomId,
      }
      axios.post('http://localhost:5555/booking',data)
          .then(alert("done"))
          .catch((err)=>console.log(err))
      
       
      
      }
    
      /* useEffect(()=>{
        axios.get('http://localhost:5555/booking')
          .then((res)=>{console.log(res.data)})
          .catch((err)=>console.log(err))
      }) */
      
    return(
        <div>
            <section className="book-header">
                   <Navbar/>
            </section>
            <section className="book-body">
            <form className='bookForm'>
                   <div className='availble-room-container'>
                      <h2>Availble Rooms</h2>
                      
                    {/*   <div className="selectOp">
                            <input type='radio' id="radioButton4" name="radioGroup" value="single" onClick={(e)=>change(e)} defaultChecked/>   
                                <label for="radioButton4" className="select">single Room</label>

                            <input type='radio' id="radioButton5" name="radioGroup" value="multiple" onClick={(e)=>change(e)}/>   
                                <label for="radioButton5" className="select" >multiple room</label>
                              
                      </div> */}
                       <h1>Availble Rooms</h1>
                      {roomInfo.map((room)=>{
                                          return(

                                               <div>
                                                 
                                                {(room.availability) ?  
                                                <div className='card-container' >
                                                    <input type="checkBox" id={room._id} name="radioGroup-room"   onClick={(e)=>getID(e)}/>   
                                                    <label for="room1" className="roomLabel">
                                                        <div className='roomCard '>
                                                          <div className='cover'>select</div>
                                                          <div className='card-details'>
                                                              <h2 className='notify display-block'>selected</h2>
                                                              <img src={room.url}/>
                                                              <ul>
                                                                <li>Room type:{room._id}</li>
                                                                <li>Price:$50/per night</li>
                                                                <li>Availblity:Available</li>
                                                              </ul>
                                                            </div>
                                                          </div>
                                                      </label>

                                                    </div> : 
                                                    <div>Sorry No rooms availbale now</div>
                                                    }
                                               </div>



                                          )
                                       }
                                    )
                      }
                       {/*   <div className='card-container' >
                            <input type="checkBox" id="room1" name="radioGroup-room" value="room"  onClick={(e)=>getID(e)}/>   
                                  <label for="room1" className="roomLabel">
                                      <div className='roomCard '>
                                         <div className='cover'>select</div>
                                       <div className='card-details'>
                                             <h2 className='notify'>selected</h2>
                                            <img src="https://media.cnn.com/api/v1/images/stellar/prod/140127103345-peninsula-shanghai-deluxe-mock-up.jpg?q=w_2226,h_1449,x_0,y_0,c_fill"/>
                                            <ul>
                                                <li>Room type:Large</li>
                                                <li>Price:$50/per night</li>
                                                <li>Availblity:Available</li>
                                            </ul>
                                          </div>
                                      </div>
                                 </label>


                           
                            
                              

                             
                            
                          </div> */}
                     
                   </div>
                   
                        <div className='form-body'>
                        <div className='left-side'>
                        <div className='container'>
                            <label for="Name">Full name</label>
                            <div >
                               <input type='text' placeholder='first name' value={firstName} onChange={(e)=>setFirstName(e.target.value)}/>
                               <input type='text' placeholder='last name' value={lastName} onChange={(e)=>setLastname(e.target.value)}/>
                        </div>
                        </div>
                       <div className='container'>
                       <label for="guest">Number of Guest</label>
                        <div >
                           <select className='dropdown' value={guestCount} onChange={(e)=>setGuestCount(e.target.value)}>
                                <option value='1'>1</option>
                                <option value='2'>2</option>
                                <option value='3'>3</option>
                                <option value='4'>4</option>

                           </select>
                            {/* <input type='Number' placeholder='count' value={guestCount} onChange={(e)=>setGuestCount(e.target.value)}/> */}

                        </div>
                       </div>


                       <div className='container'>
                       <label for="guest">Arrival</label>
                        <div className='section-left'>
                          <input type='date' placeholder='arrival data' value={arrivalDate} onChange={(e)=>setArrivalDate(e.target.value)}/>
                          <input type='time' placeholder='arrival time'  value={arrivalTime} onChange={(e)=>setArrivalTime(e.target.value)}/>
                        </div>
                       </div>

                       <div className='container'>
                       <label for="guest">Departure</label>
                        <div className='section-left'>
                        <input type='date' placeholder='departure data' value={departureDate} onChange={(e)=>setDepartureDate(e.target.value)}/>
                          <input type='time' placeholder='departure time'  value={departureTime} onChange={(e)=>setDepartureTime(e.target.value)}/>
                        </div>
                       </div>

                       <div className='container '>
                       <label for="guest">Do you want to pickup?</label>
                        <div className='section-left pickMe'>
                          <input type='radio'   name='pick' value={true} onChange={(e)=>setPickup(e.target.value)}/>Yes
                          <input type='radio'  name='pick' value={false} onChange={(e)=>setPickup(e.target.value)}/>No
                        </div>
                       </div>


                        </div>
                        <div className='right-side'>
                            <div className='container'>
                            <label for="mail">E-mail</label>
                            <div>
                            <input type='mail' placeholder='example@gmail.com' value={mail} onChange={(e)=>setMail(e.target.value)}></input>
                            </div>
                            </div>
                           <div className='container'>
                           <label for="tp">Contact number</label>
                            <div>
                            <input type='tel' placeholder='contact number' value={contactNo} onChange={(e)=>setContactNo(e.target.value)}></input>
                            </div>
                           </div>
                        </div>
                        </div>
                        <div className='form-footer'>
                         <button className='btnSubmit' onClick={createBooking}>Submit</button>
                        
                        </div>

                   </form>
               
                
            </section>
            <section className="book-footer">
             
               
            </section>
        </div>
    )
}
