import axios from "axios";
import React, { useEffect, useState } from "react";


export default function Img(){
    let hasAvailableRoom=true

    const [roomData,setRoomData]=useState([]);
    const [roomId,setRoomId]=useState('');
    const [selectedRoom, setSelectedRoom] = useState('');
    const [rooms, setRooms] = useState([]);
    const [inputType,setInputType]=useState(false);
    useEffect(()=>{
        axios.get('http://localhost:5555/Rooms')
          .then((res)=>{
        
           setRoomData(res.data);
           
            })
          .catch((err)=>console.log(err))
      }) 

      const changeAvailable=(event)=>{
          /* const id=event.target.id;
          setRoomId(id);
          axios.put(`http://localhost:5555/Rooms/:${roomId}`,async(req,res)=>{
            try{
              }
            catch(err){
               return res.send({message:err.message})
            } */
            axios.get('http://localhost:5555/Rooms')
              .then((res)=>{console.log(res.data)})
              .catch((err)=>{console.log(err)}) 
          } 
          
          const handleRadioChange = (event,roomId) => {
            const rId=event.target.id;
            setSelectedRoom(roomId);
            setRooms([roomId])
            if(inputType){
              if (!rooms.includes(roomId)) {
                setRooms([roomId]);
               } else {
                 setRooms(prevIds => prevIds.filter(room => room !== roomId));
               }
            }
            else{
              if (!rooms.includes(roomId)) {
                setRooms(prevIds => [...prevIds, roomId]);
               } else {
                 setRooms(prevIds => prevIds.filter(room => room !== roomId));
               }
            }
            console.log(`Room:${rID}`)
        };
        
        useEffect(()=>{
          {}
        }
        )


      return(
        <div>
           Available Rooms
            {roomData.map((room)=>{
                let hasAvailableRoom = room.availability;
                return(
                    <div key={room._id}>
                      
                       {hasAvailableRoom ? 
                            <div>
                         
                              <form>
                                {inputType ?
                                      <div>
                                        <input 
                                              type='radio' 
                                              id={room._id}
                                              name='room'
                                              checked={ selectedRoom === room._id}
                                              onChange={(e) => handleRadioChange(e,room._id)} 
                                          />
                                          <label for={room._id}>
                                              <img src={room.url}/>
                                          </label>
                                      </div>
                                      :

                                      <div>
                                        <input 
                                              type='checkBox' 
                                              id={room._id}
                                              name='room'
                                            
                                              onChange={() => handleRadioChange(room._id)} 
                                          />
                                          <label for={room._id}>
                                              <img src={room.url}/>
                                          </label>
                                      </div>
                                  }
                              </form>
                     
                            </div> 
                            : 
                            <span>Sorry</span>  
                        
                        
                        
                        
                      }
                      


                    </div>
                )
            })}
      
        </div>
      )
}
