import './book.css';
import Navbar from '../Components/navbar.js';
import {useEffect, useRef, useState} from 'react';
import Room1 from '../Images/room1.jpg';
import Room2 from '../Images/room2.png';
import axios from 'axios';
import ImageCarousel from "../Components/ImageCarousel";
export default function Book(){
   
   
    const [firstName,setFirstName]=useState('')
    const [lastName,setLastname]=useState('')
    const [mail,setMail]=useState('')
    const [guestCount,setGuestCount]=useState();
    const [arrivalDate,setArrivalDate]=useState('');
    const [arrivalTime,setArrivalTime]=useState('');
    const [isRoomAvailable,setisRoomAvailable]=useState(true);
    const [pickup,setPickup]=useState(true)
    const [contactNo,setContactNo]=useState();
    const [departureDate,setDepartureDate]=useState('');
    const [departureTime,setDepartureTime]=useState('');
    const [roomId,setRoomId]=useState([]);
    const [isClicked,setIsClicked]=useState(false);
    const[inputType,changeInputType]=useState('radio');
    const[roomInfo,setRoomInfo]=useState([]);
  
    const change=()=>{
     
      if(inputType==='radio'){
        changeInputType('checkBox')
      }
      if(inputType==='checkBox'){
        changeInputType('radio')
      }
    }
    const handleClick=(id)=>{
     
      if (!roomId.includes(id)) {
        setRoomId(prevIds => [...prevIds, id]);
      } else {
        setRoomId(prevIds => prevIds.filter(room => room !== id));
      }
    
    }
   
    
    //get rooms info
    useEffect(()=>{
      axios('http://localhost:5555/Rooms')
      .then((res)=>{
          let availableRoomCount=0;
            setRoomInfo(res.data);
            res.data.forEach((r)=>{
              if(r.availability ==="available"){
                availableRoomCount++;
                }
            })
            if(availableRoomCount===0){
              setisRoomAvailable(false)
            }
            console.log(`available room count: ${availableRoomCount}`)
      })
      .catch((err)=>{
        console.log(err.res);
      })
     
    },[])
    
    

    
    
    const createBooking=(e)=>{
      let isAllCorrect=true;
      if(firstName.length===0)
          { isAllCorrect=false;
            alert('please enter your first name')}
      
      const regex = /^[^\s@]+@gmail\.com$/;
      if (!regex.test(mail)) {
        isAllCorrect=false;
        alert('Please enter a valid email address')
      }
      const telephoneRegex = /^\d{10}$/; // Assumes a 10-digit telephone number
      if (!telephoneRegex.test(contactNo)) {
        isAllCorrect=false;
        alert('Contact number is not valid')
      }
      if(guestCount===0){
        isAllCorrect=false;
        alert('please select guest numbers')
      }
      if(arrivalDate===''){
        isAllCorrect=false;
        alert('please select the date of your arrival')
      }
      if(arrivalTime===''){
        isAllCorrect=false;
        alert('please select the Time of your arrival')
      }
      if(departureDate===''){
        isAllCorrect=false;
        alert('please select the date of your departure')
      }
      if(departureTime===''){
        isAllCorrect=false;
        alert('please select the time of your departure')
      }
      /* const data={
        firstName,
        lastName,
        mail,
        guestCount,
        arrivalDate,
        arrivalTime,
        departureDate,
        departureTime,
        pickup,
        contactNo,
        roomId,
      }
      axios.post('http://localhost:5555/booking',data)
          .then(console.log("done"))
          .catch((err)=>console.log(err))
      
         
      //update room availability
      roomId.forEach((id)=>{
          axios.get(`http://localhost:5555/Rooms/${id}`)
                .then((res)=>{
                    const url=res.data.url;
                    console.log(res.data)
                    const availability="not available";
