                            </div>
                            </div>
                           <div className='container'>
                           <label for="tp">Contact number</label>
                            <div>
                            <input type='tel' placeholder='contact number' value={contactNo} onChange={(e)=>setContactNo(e.target.value)}></input>
                            </div>
                           </div>
                        </div>
                        </div>
                        <div className='form-footer'>
                        {/*  <button className='btnSubmit' onClick={createBooking}>Submit</button> */}
                         
                        </div>
                       
                   </form>
                   <div className='btnSubmit'>
                   <button  onClick={createBooking}>Submit</button>
                   </div>
                  
            </section>
            <section className="book-footer">
             
           
            </section>
        </div>
    )
