import './book.css';
import Navbar from '../Components/navbar.js';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
export default function Book(){
    const [fName,setFname]=useState('');
    const [lName,setLname]=useState('');
    const [mail,setMail]=useState('');
    const [guestCount,setGuestCount]=useState();
   /*  const [roomCount,setRoomCount]=useState();
   
    const [arrivalTime,setArrivalTime]=useState();
    const [arrivalDate,setArrivalDate]=useState();
    const [departureTime,setDepartureTime]=useState();
    const [departureDate,setDepartureDate]=useState();
    const [pickUp,setPickUp]=useState(false); */
    const navigate=useNavigate()
    
    const handleBookNow=()=>{
      const data={
        fName,
        lName,
        mail,
        guestCount
      }
      axios.post('http://localhost:5555/booking',data)
            .then(alert("done"))
            .catch((err)=>{
              console.log('An error occured');
            })
    }
    const showBooking=()=>{
      const data={
        fName,
        lName,
        mail,
        guestCount
      }
      axios.post('http://localhost:5555/booking',data)
            .then(alert("done"))
            .catch((err)=>{
              console.log('An error occured');
            })
    }

    return(
        <div>
            <section className="book-header">
                   <Navbar/>
            </section>
            <section className="book-body">
                   
                   <form className='bookForm'>
                        <div className='form-body'>
                        <div className='left-side'>
                        <label for="Name">Full name</label><br/>
                        <div className='section-left'>
                            <input type='text' placeholder='first name' value={fName} onChange={(e)=>setFname(e.target.value)}/>
                            <input type='text' placeholder='last name' value={lName} onChange={(e)=>setLname(e.target.value)}/>
                        </div>
                        <label for="guest">Number of Guest</label><br/>
                        <div className='section-left'>
                           <select className='dropdown' value={guestCount} onChange={(e)=>setGuestCount(e.target.guestCount)}>
                                <option value='1'>1</option>
                                <option value='2'>2</option>
                                <option value='3'>3</option>
                                <option value='4'>4</option>

                           </select>
                        </div>


                        <label for="guest">Arrival</label><br/>
                        <div className='section-left'>
                          <input type='date' placeholder='arrival data'/>
                          <input type='time' placeholder='arrival time'/>
                        </div>

                        <label for="guest">Departure</label><br/>
                        <div className='section-left'>
                          <input type='date' placeholder='departure data'/>
                          <input type='time' placeholder='departure time'/>
                        </div>

                        <label for="guest">pickup</label><br/>
                        <div className='section-left'>
                          <input type='checkbox' value='checked'/>Yes
                          <input type='checkbox' />No
                        </div>


                        </div>
                        <div className='right-side'>
                            <label for="mail">E-mail</label>
                            <div>
                            <input type='mail' placeholder='example@gmail.com' value={mail} onChange={(e)=>setMail(e.target.value)}></input>
                            </div>
                            <label for="tp">Contact number</label>
                            <div>
                            <input type='tel' placeholder='contact number'></input>
                            </div>
                        </div>
                        </div>
                        <div className='form-footer'>
                         <button className='btnSubmit' onClick={handleBookNow}>Submit</button>
                        </div>

                   </form>
            </section>
            <section className="book-footer">
                FOOTER
            </section>
        </div>
    )
}
