              .catch((err)=>{console.log(err)}) 
          } 
          
      



      return(
        <div>
           Available Rooms
            {roomData.map((room)=>{
                if(!room.availability){
                    hasAvailableRoom=true;
                  }
                return(
                    <div key={room._id}>
                      
                       {hasAvailableRoom ? <div>
                         
                         <form>
                            <input type="radio" id={room._id} name='' onChange={(e)=>{changeAvailable(e)}} />
